import React, { useState } from 'react';

function Checkout() {
  const [shippingAddress, setShippingAddress] = useState({});
  const [paymentMethod, setPaymentMethod] = useState(''); // Store selected payment method

  // Handle user input for shipping address
  const handleShippingAddressChange = (e) => {
    const { name, value } = e.target;
    setShippingAddress({ ...shippingAddress, [name]: value });
  };

  // Handle payment method selection
  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  // Handle payment processing
  const handlePayment = () => {
    // Implement payment processing logic based on the selected payment method
    if (paymentMethod === 'mpesa') {
      // Process M-Pesa payment
    } else if (paymentMethod === 'creditCard') {
      // Process credit card payment
    } else if (paymentMethod === 'bank') {
      // Process bank payment
    }
  };

  return (
    <div>
      <h2>Checkout</h2>
      <form>
        {/* Shipping Address */}
        <div>
          <label>Shipping Address:</label>
          <input
            type="text"
            name="address"
            value={shippingAddress.address || ''}
            onChange={handleShippingAddressChange}
          />
          {/* Add more fields for shipping information */}
        </div>

        {/* Payment Method */}
        <div>
          <label>Payment Method:</label>
          <select onChange={handlePaymentMethodChange}>
            <option value="">Select Payment Method</option>
            <option value="mpesa">M-Pesa</option>
            <option value="creditCard">Credit Card</option>
            <option value="bank">Bank</option>
          </select>
        </div>

        {/* Payment Button */}
        <button onClick={handlePayment}>Proceed to Payment</button>
      </form>
    </div>
  );
}

export default Checkout;
